from typing import Any

from langchain.tools import StructuredTool
from pydantic import BaseModel, Field
from langflow.base.langchain_utilities.model import LCToolComponent
from langflow.field_typing import Tool
from langflow.inputs import MessageTextInput, SecretStrInput
from langflow.schema import Data
import yfinance as yf


# Define the component class for Yahoo Finance Crypto API
class YahooFinanceCryptoComponent(LCToolComponent):
    display_name: str = "Yahoo Finance Crypto"
    description: str = "Retrieve cryptocurrency and blockchain-related data using Yahoo Finance."
    name = "YahooFinanceCrypto"
    documentation: str = "https://www.yahoofinanceapi.com/"

    # Define the inputs needed for this component
    inputs = [
        MessageTextInput(name="crypto_symbol", display_name="Cryptocurrency Symbol", required=True),
        MessageTextInput(name="metric", display_name="Financial Metric", required=False, value="summary"),
    ]

    # Define the schema for the API tool arguments
    class YahooFinanceCryptoSchema(BaseModel):
        crypto_symbol: str = Field(..., description="The cryptocurrency symbol (e.g., BTC-USD for Bitcoin).")
        metric: str = Field("summary", description="The financial metric to retrieve (e.g., 'summary', 'price').")

    # Define the API Wrapper for Yahoo Finance
    class YahooFinanceCryptoWrapper:
        def __init__(self, crypto_symbol: str, metric: str = "summary"):
            self.crypto_symbol = crypto_symbol
            self.metric = metric
            self.ticker = yf.Ticker(crypto_symbol)  # Initialize with the cryptocurrency symbol

        def get_crypto_data(self) -> dict[str, Any]:
            # Based on the metric, retrieve the corresponding financial data
            if self.metric == "summary":
                return self.ticker.info  # Fetch general summary info
            elif self.metric == "price":
                return {"price": self.ticker.history(period="1d")["Close"].iloc[-1]}
            elif self.metric == "history":
                return self.ticker.history(period="1mo")  # Fetch price history
            else:
                raise ValueError("Invalid metric. Please choose 'summary', 'price', or 'history'.")

    # Build the API wrapper
    def _build_wrapper(self, crypto_symbol: str, metric: str):
        return self.YahooFinanceCryptoWrapper(crypto_symbol=crypto_symbol, metric=metric)

    # Tool builder function
    def build_tool(self) -> Tool:
        def get_crypto_data(crypto_symbol: str, metric: str = "summary") -> dict[str, Any]:
            wrapper = self._build_wrapper(crypto_symbol=crypto_symbol, metric=metric)
            return wrapper.get_crypto_data()

        tool = StructuredTool.from_function(
            name="yahoo_finance_crypto",
            description="Fetch cryptocurrency data using Yahoo Finance.",
            func=get_crypto_data,
            args_schema=self.YahooFinanceCryptoSchema,
        )

        self.status = "Yahoo Finance Crypto Tool created successfully."
        return tool

    # Run model function to trigger the API call
    def run_model(self) -> list[Data]:
        tool = self.build_tool()
        # Retrieve and pass all necessary inputs
        results = tool.run(
            {
                "crypto_symbol": self.crypto_symbol,
                "metric": self.metric,
            }
        )

        # Format the results for output
        if self.metric == "summary":
            formatted_results = [Data(data=results, text=f"Summary: {results}")]
        elif self.metric == "price":
            formatted_results = [Data(data=results, text=f"Latest Price: {results['price']}")]
        elif self.metric == "history":
            formatted_results = [
                Data(data=results, text=f"Price History: {results[['Close']].to_dict(orient='list')}")
            ]
        else:
            formatted_results = [Data(data=results, text="No valid data found.")]

        self.status = formatted_results
        return formatted_results

from typing import Any, Dict
from pydantic import BaseModel, Field
from langflow.base.langchain_utilities.model import LCToolComponent
from langflow.inputs import MessageTextInput
from langflow.schema import Data
import requests

class CoinGeckoComponent(LCToolComponent):
    display_name: str = "CoinGecko API"
    description: str = "Retrieve cryptocurrency data using the CoinGecko API."
    name = "CoinGeckoAPI"
    documentation: str = "https://www.coingecko.com/en/api"

    # Define the inputs needed for this component
    inputs = [
        MessageTextInput(name="crypto_id", display_name="Cryptocurrency ID", required=True),
        MessageTextInput(name="currency", display_name="Fiat Currency", required=False, value="usd"),
        MessageTextInput(name="metric", display_name="Metric Type", required=False, value="price"),
    ]

    # Define the schema for the API tool arguments
    class CoinGeckoSchema(BaseModel):
        crypto_id: str = Field(..., description="The ID of the cryptocurrency (e.g., 'bitcoin', 'ethereum').")
        currency: str = Field("usd", description="The fiat currency to convert the price into (default is 'usd').")
        metric: str = Field("price", description="The type of metric to retrieve (e.g., 'price', 'market_cap').")

    # Define the API Wrapper for CoinGecko
    class CoinGeckoAPIWrapper:
        BASE_URL = "https://api.coingecko.com/api/v3"

        def __init__(self, crypto_id: str, currency: str = "usd", metric: str = "price"):
            self.crypto_id = crypto_id
            self.currency = currency
            self.metric = metric

        def get_crypto_data(self) -> Dict[str, Any]:
            url = f"{self.BASE_URL}/simple/price"
            params = {
                "ids": self.crypto_id,
                "vs_currencies": self.currency,
                "include_market_cap": "true" if self.metric == "market_cap" else "false",
                "include_24hr_vol": "true" if self.metric == "volume" else "false",
                "include_24hr_change": "true" if self.metric == "price_change" else "false",
            }

            try:
                response = requests.get(url, params=params)

                if response.status_code == 429:
                    # Rate limiting occurred, return a safe message without throwing an exception
                    print("Rate limit exceeded. Please try again later.")
                    return {"error": "Rate limit exceeded"}

                if response.status_code != 200:
                    raise Exception(f"Error {response.status_code}: {response.text}")

                data = response.json()

                if self.crypto_id not in data:
                    raise ValueError(f"Cryptocurrency '{self.crypto_id}' not found.")

                return data[self.crypto_id]

            except Exception as e:
                # Catch any other exceptions and handle them gracefully
                print(f"An error occurred: {e}")
                return {"error": str(e)}

    # Build the API wrapper
    def _build_wrapper(self, crypto_id: str, currency: str, metric: str):
        return self.CoinGeckoAPIWrapper(crypto_id=crypto_id, currency=currency, metric=metric)

    # Tool builder function
    def build_tool(self) -> Tool:
        def get_crypto_data(crypto_id: str, currency: str = "usd", metric: str = "price") -> Dict[str, Any]:
            wrapper = self._build_wrapper(crypto_id=crypto_id, currency=currency, metric=metric)
            return wrapper.get_crypto_data()

        tool = StructuredTool.from_function(
            name="coingecko_api",
            description="Fetch cryptocurrency data using the CoinGecko API.",
            func=get_crypto_data,
            args_schema=self.CoinGeckoSchema,
        )

        self.status = "CoinGecko API Tool created successfully."
        return tool

    # Run model function to trigger the API call
    def run_model(self) -> list[Data]:
        tool = self.build_tool()

        # Fetch the CoinGecko cryptocurrency data based on user inputs
        results = tool.run(
            {
                "crypto_id": self.crypto_id,
                "currency": self.currency,
                "metric": self.metric,
            }
        )

        # Handle rate limit or errors gracefully
        if "error" in results:
            return [Data(data=results, text=results["error"])]

        # Format the results for output
        if self.metric == "price":
            formatted_result = Data(data=results, text=f"Price: {results[self.currency]}")
        elif self.metric == "market_cap":
            formatted_result = Data(data=results, text=f"Market Cap: {results[f'{self.currency}_market_cap']}")
        elif self.metric == "volume":
            formatted_result = Data(data=results, text=f"24h Volume: {results[f'{self.currency}_24h_vol']}")
        elif self.metric == "price_change":
            formatted_result = Data(data=results, text=f"24h Price Change: {results[f'{self.currency}_24h_change']}")
        else:
            formatted_result = Data(data=results, text="No valid data found.")

        self.status = [formatted_result]
        return [formatted_result]

from typing import Any
from langchain.tools import StructuredTool
from pydantic import BaseModel, Field
from langflow.base.langchain_utilities.model import LCToolComponent
from langflow.field_typing import Tool
from langflow.inputs import DictInput, IntInput, MultilineInput, SecretStrInput
from langflow.schema import Data
import requests

class CoinMarketCapAPIComponent(LCToolComponent):
    display_name: str = "CoinMarketCap API"
    description: str = "Retrieve latest cryptocurrency data from CoinMarketCap."
    name = "CoinMarketCapAPI"
    documentation: str = "https://coinmarketcap.com/api/documentation/v1/"

    class CoinMarketCapAPIWrapper:
        """Wrapper class to call CoinMarketCap API and retrieve cryptocurrency data."""
        def __init__(self, api_key: str):
            self.api_key = api_key
            self.url = "https://pro-api.coinmarketcap.com/v1/cryptocurrency/listings/latest"
            self.headers = {
                "Accepts": "application/json",
                "X-CMC_PRO_API_KEY": self.api_key,
            }
    
        def get_cryptocurrency_data(self, start: int = 1, limit: int = 10) -> list[dict[str, Any]]:
            parameters = {"start": start, "limit": limit, "convert": "USD"}
            response = requests.get(self.url, headers=self.headers, params=parameters)
            data = response.json()
    
            if response.status_code != 200:
                raise Exception(f"Error {response.status_code}: {data.get('status', {}).get('error_message', 'Unknown error')}")
    
            return data["data"]
    
    # Define the inputs needed for this component
    inputs = [
        SecretStrInput(name="api_key", display_name="CoinMarketCap API Key", required=True),
        IntInput(name="start", display_name="Start", value=1 , required=True),
        IntInput(name="limit", display_name="Limit", value=10, required=True),
    ]
    
        # Define the schema for the API tool arguments
    class CoinMarketCapSchema(BaseModel):
        start: int = Field(1, description="The rank from which to start the listing")
        limit: int = Field(10, description="The number of cryptocurrencies to retrieve")
    
    
    # Helper function to build the API wrapper
    def _build_wrapper(self):
        return self.CoinMarketCapAPIWrapper(api_key=self.api_key)

    # Tool builder function
    def build_tool(self) -> Tool:
        wrapper = self._build_wrapper()

        def get_crypto_data(
            start: int = 1, limit: int = 10
        ) -> list[dict[str, Any]]:
            # Call the wrapper function to get cryptocurrency data
            return wrapper.get_cryptocurrency_data(start=start, limit=limit)

        # Return the StructuredTool
        tool = StructuredTool.from_function(
            name="coinmarketcap_api",
            description="Fetch latest cryptocurrency data from CoinMarketCap API",
            func=get_crypto_data,
            args_schema=self.CoinMarketCapSchema,
        )

        self.status = f"CoinMarketCap API Tool created with API Key."
        return tool

    # Run model function to trigger the API call
    def run_model(self) -> list[Data]:
        tool = self.build_tool()
        results = tool.run(
            {
                "start": self.start,
                "limit": self.limit,
            }
        )

        data_list = [Data(data=result, text=f"Name: {result['name']}, Price: {result['quote']['USD']['price']}") for result in results]

        self.status = data_list
        return data_list
